import React, {useState} from 'react';
import './App.css';
import {Map} from './01/Map';
import {Button} from './01/components/Button';
import {UseState} from "./01/UseState";
import {Filter, NameButtonType} from "./01/Filter";
import {FirstLesson} from "./01";

function App() {
    const topCars = [
        {manufacturer: 'BMW', model: 'm5cs'},
        {manufacturer: 'Mercedes', model: 'e63s'},
        {manufacturer: 'Audi', model: 'rs6'}
    ]

    const ButtonFoo = (subscriber: string) => {
        console.log(subscriber)
    }

    const [money, setMoney] = useState([
        {banknots: 'Dollars', value: 100, number: ' a1234567890'},
        {banknots: 'Dollars', value: 50, number: ' z1234567890'},
        {banknots: 'RUBLS', value: 100, number: ' w1234567890'},
        {banknots: 'Dollars', value: 100, number: ' e1234567890'},
        {banknots: 'Dollars', value: 50, number: ' c1234567890'},
        {banknots: 'RUBLS', value: 100, number: ' r1234567890'},
        {banknots: 'Dollars', value: 50, number: ' x1234567890'},
        {banknots: 'RUBLS', value: 50, number: ' v1234567890'},
    ])

    let [filter, setFilter] = useState<NameButtonType>('All');

    let currentMoney = money;

    if (filter === 'Ruble') {
        currentMoney = money.filter(m => m.banknots === 'RUBLS')
    }
    if (filter === 'Dollar') {
        currentMoney = money.filter(m => m.banknots === 'Dollars')
    }

    const onClickFilterHandler = (nameButton: NameButtonType) => {
        setFilter(nameButton);
    }
    return (
        <>
            <FirstLesson />
        </>
    );
}

export default App;
